import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import axios from 'axios';
import styled from 'styled-components';
import VideoFeed from './components/VideoFeed';
import StatusPanel from './components/StatusPanel';
import ControlsPanel from './components/ControlsPanel';
import AudioVisualizer from './components/AudioVisualizer';
import './App.css';

const AppContainer = styled.div`
  display: flex;
  height: 100vh;
  background: #1a1a1a;
  color: white;
  font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
`;

const MainContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const Sidebar = styled.div`
  width: 350px;
  background: #2a2a2a;
  padding: 20px;
  border-left: 1px solid #444;
  overflow-y: auto;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
  padding-bottom: 20px;
  border-bottom: 2px solid #444;
`;

const Title = styled.h1`
  color: #00ff88;
  margin: 0;
  font-size: 24px;
`;

const StatusIndicator = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 8px 16px;
  background: ${props => props.status === 'active' ? '#00ff88' : props.status === 'error' ? '#ff4444' : '#666'};
  border-radius: 20px;
  font-size: 14px;
  font-weight: bold;
`;

function App() {
  // WebSocket connection
  const [socket, setSocket] = useState(null);
  const [connected, setConnected] = useState(false);

  // System state
  const [cameraActive, setCameraActive] = useState(false);
  const [audioActive, setAudioActive] = useState(false);
  const [analysisActive, setAnalysisActive] = useState(false);

  // Data state
  const [currentFrame, setCurrentFrame] = useState(null);
  const [patients, setPatients] = useState([]);
  const [triageDecisions, setTriageDecisions] = useState([]);
  const [audioAnalysis, setAudioAnalysis] = useState(null);
  const [fps, setFps] = useState(0);
  const [systemStatus, setSystemStatus] = useState({});

  // Refs
  const frameIntervalRef = useRef(null);

  // Initialize WebSocket connection
  useEffect(() => {
    const newSocket = io('ws://localhost:8001');

    newSocket.on('connect', () => {
      console.log('ðŸ”— Connected to backend');
      setConnected(true);
    });

    newSocket.on('disconnect', () => {
      console.log('ðŸ”Œ Disconnected from backend');
      setConnected(false);
    });

    newSocket.on('frame_update', (data) => {
      setCurrentFrame(data.frame);
      setPatients(data.patients || []);
      setTriageDecisions(data.triage_decisions || []);
      setAudioAnalysis(data.audio_analysis);
      setFps(data.fps || 0);
    });

    newSocket.on('analysis_update', (data) => {
      setCurrentFrame(data.frame);
      setPatients(data.patients || []);
      setTriageDecisions(data.triage_decisions || []);
      setAudioAnalysis(data.audio_analysis);
      setFps(data.fps || 0);
    });

    newSocket.on('camera_status', (data) => {
      setCameraActive(data.data.status === 'success');
    });

    newSocket.on('audio_status', (data) => {
      setAudioActive(data.data.status === 'success');
    });

    newSocket.on('analysis_status', (data) => {
      setAnalysisActive(data.data.status === 'success');
    });

    setSocket(newSocket);

    return () => {
      newSocket.close();
    };
  }, []);

  // Fetch system status periodically
  useEffect(() => {
    const fetchStatus = async () => {
      try {
        const response = await axios.get('http://localhost:8001/api/status');
        setSystemStatus(response.data);
      } catch (error) {
        console.error('Error fetching status:', error);
      }
    };

    fetchStatus();
    const interval = setInterval(fetchStatus, 5000);

    return () => clearInterval(interval);
  }, []);

  // Start continuous frame processing
  const startFrameProcessing = () => {
    if (frameIntervalRef.current) return;

    frameIntervalRef.current = setInterval(async () => {
      try {
        const response = await axios.post('http://localhost:8001/api/process_frame');
        if (response.data.status === 'success') {
          setCurrentFrame(response.data.frame);
          setPatients(response.data.patients || []);
          setTriageDecisions(response.data.triage_decisions || []);
          setAudioAnalysis(response.data.audio_analysis);
          setFps(response.data.fps || 0);
        }
      } catch (error) {
        console.error('Error processing frame:', error);
      }
    }, 100); // Process every 100ms for smooth video
  };

  // Stop continuous frame processing
  const stopFrameProcessing = () => {
    if (frameIntervalRef.current) {
      clearInterval(frameIntervalRef.current);
      frameIntervalRef.current = null;
    }
  };

  // Handle camera start
  const handleStartCamera = async () => {
    try {
      const response = await axios.post('http://localhost:8001/api/camera/start');
      if (response.data.status === 'success') {
        startFrameProcessing();
      }
    } catch (error) {
      console.error('Error starting camera:', error);
    }
  };

  // Handle camera stop
  const handleStopCamera = async () => {
    try {
      stopFrameProcessing();
      await axios.post('http://localhost:8000/api/camera/stop');
      setCurrentFrame(null);
      setPatients([]);
      setTriageDecisions([]);
    } catch (error) {
      console.error('Error stopping camera:', error);
    }
  };

  // Handle audio start
  const handleStartAudio = async () => {
    try {
      await axios.post('http://localhost:8000/api/audio/start');
    } catch (error) {
      console.error('Error starting audio:', error);
    }
  };

  // Handle audio stop
  const handleStopAudio = async () => {
    try {
      await axios.post('http://localhost:8000/api/audio/stop');
    } catch (error) {
      console.error('Error stopping audio:', error);
    }
  };

  // Handle analysis start
  const handleStartAnalysis = async () => {
    try {
      await axios.post('http://localhost:8000/api/analysis/start');
    } catch (error) {
      console.error('Error starting analysis:', error);
    }
  };

  // Handle analysis stop
  const handleStopAnalysis = async () => {
    try {
      await axios.post('http://localhost:8000/api/analysis/stop');
    } catch (error) {
      console.error('Error stopping analysis:', error);
    }
  };

  // Export logs
  const handleExportLogs = async (format) => {
    try {
      const response = await axios.post(`http://localhost:8000/api/export/${format}`);
      if (response.data.status === 'success') {
        // Download the file
        window.open(`http://localhost:8000/api/download/${response.data.filename}`, '_blank');
      }
    } catch (error) {
      console.error('Error exporting logs:', error);
    }
  };

  return (
    <AppContainer>
      <MainContent>
        <Header>
          <Title>ðŸš‘ RescueLens - AI-Assisted Triage</Title>
          <StatusIndicator status={connected ? 'active' : 'error'}>
            {connected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
          </StatusIndicator>
        </Header>

        <VideoFeed 
          frame={currentFrame}
          patients={patients}
          triageDecisions={triageDecisions}
          fps={fps}
        />

        <ControlsPanel
          cameraActive={cameraActive}
          audioActive={audioActive}
          analysisActive={analysisActive}
          onStartCamera={handleStartCamera}
          onStopCamera={handleStopCamera}
          onStartAudio={handleStartAudio}
          onStopAudio={handleStopAudio}
          onStartAnalysis={handleStartAnalysis}
          onStopAnalysis={handleStopAnalysis}
          onExportLogs={handleExportLogs}
        />
      </MainContent>

      <Sidebar>
        <StatusPanel
          patients={patients}
          triageDecisions={triageDecisions}
          audioAnalysis={audioAnalysis}
          systemStatus={systemStatus}
        />

        <AudioVisualizer
          audioAnalysis={audioAnalysis}
          audioActive={audioActive}
        />
      </Sidebar>
    </AppContainer>
  );
}

export default App;
